import axios from "axios";
import { useEffect, useState } from "react";
import { storage } from "./fire";
import Showdata from "./Showdata";
import { database } from "./fire";
import firebase from "firebase";

function Fileupload() {
    
  
  const [progress, setProgress] = useState(0);
  const [post, setPost] = useState(null);
  const[baseUrl,setBaseUrl]=useState('');
  const[toggle,setToggle]=useState(false);
  const [info , setInfo] = useState([]);

  let fileReader;
    
  const handleFileRead = (e) => {
    const content =[JSON.parse(fileReader.result)];
    console.log(content);
    console.log(typeof(content));
    console.log((content[0][0]).title);
       content.map(writeUserData);

       function writeUserData(item) {
        var postListRef = firebase.database().ref('posts');
  var newPostRef = postListRef.push();
  newPostRef.set({    
      item  });
      };
 
      const Fetchdata = ()=>{
        database.collection("posts").get().then((querySnapshot) => {
             
            // Loop through the data and store
            // it in array to display
            querySnapshot.forEach(element => {
                var data = element.data();
                setInfo(arr => [...arr , data]);
                console.log(info);
                  
            });
        })
      }

      Fetchdata();
  };


  const formHandler = (e) => {
    e.preventDefault();
    const file = e.target[0].files[0];
    console.log(file);
    handleFileChosen(file);
    uploadFiles(file);
  };
 



  const uploadFiles = (file) => {
    //
    const uploadTask = storage.ref(`files/${file.name}`).put(file);
    uploadTask.on(
      "state_changed",
      (snapshot) => {
        //
        const prog = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(prog);
      },
      (error) => console.log(error),
      () => {
        storage
          .ref("files")
          .child(file.name)
          .getDownloadURL()
          .then((url) => {
            console.log(url)          
                   
          })          
            
          });
      }
    
      const handleFileChosen = (file) => {
        fileReader = new FileReader();
        fileReader.onloadend = handleFileRead;
        fileReader.readAsText(file);
      };
  


function view(baseurl){
       console.log(baseUrl);
       axios.get(baseUrl).then((response) => {
        setPost(response.data);

      })
    }   
     
            


  return (
    <div className="Fileupload">
      <form onSubmit={formHandler}>
        <input type="file" className="input" />
        <button type="submit">Upload</button>
      </form>
      <hr />
     
      <p>Uploading done {progress}%</p><br></br>
      <p>{progress===100?"click view Tab":"Waiting"
          }</p>
          {progress===100 && <button onClick={()=><Showdata/>}>view</button>}
    </div>
  );
}

export default Fileupload;
