import firebase from 'firebase';

const Showdata=(data)=> {
   
    var starCountRef = firebase.database().ref('posts/' );
    starCountRef.on('value', (snapshot) => {
               const dbdata=(snapshot.val());
      console.log(dbdata);
      console.log(typeof(dbdata));
      
    
    });

    // firebase.database().ref('posta').startAt(searchBoxInput).orderByChild('title')
    // .on('child_added', function (snapshot) {
    // console.log("All Data Search2:", snapshot.val());
// })

    return(
<table>
    <thead>
  <tr style={{backgroundColor:'darkgrey'}}>
    <th>User</th>
    <th>Title</th>
    <th>Content</th>
  </tr></thead>
  <tbody>
      {data.map((data)=>(
           <tr key={data.id}>
           <td>data.userId</td>
           <td>data.tile</td>
           <td>data.body</td>
         </tr>

      ))}
 
  </tbody>
  
</table>

    )
       
   
}

export default Showdata;
