import axios from "axios";
import { useEffect, useState } from "react";
import { storage } from "./fire";
import Showdata from "./Showdata";
import { database } from "./fire";


function Fileupload() {
    
  
  const [progress, setProgress] = useState(0);
  const [post, setPost] = useState(null);
  const[baseUrl,setBaseUrl]=useState('');
  const[toggle,setToggle]=useState(false);

  let fileReader;
    
  const handleFileRead = (e) => {
    const content =[JSON.parse(fileReader.result)];
    console.log(content);
    console.log(typeof(content));
    console.log((content[0][1]));
       setPost(content);
    
  };
//   var playersRef = database().ref("login-finance-peer-default-rtdb/");

// playersRef.set ({
//    John: {
//       number: 1,
//       age: 30
//    },
	
//    Amanda: {
//       number: 2,
//       age: 20
//    }
// });

  const formHandler = (e) => {
    e.preventDefault();
    const file = e.target[0].files[0];
    console.log(file);
    handleFileChosen(file);
    uploadFiles(file);
  };
 
 useEffect=()=>{
  var firebaseRef = database()
  var data = {
    username:"wadiemendja",
    password:"UserPassword"
}
firebaseRef.ref('users/')
firebaseRef.push(data)};

//   curl -X PUT -d '{
//  "eantoni": {
//    "name": "Erika Antoni",
//    "state": "California",
//    "emails": {
//      "primary": "eantoni@example.com",
//      "secondary": "erika@work.com"
//    }
//  }' }'https://{db-name}.firebaseio.com/users.json'

  const uploadFiles = (file) => {
    //
    const uploadTask = storage.ref(`files/${file.name}`).put(file);
    uploadTask.on(
      "state_changed",
      (snapshot) => {
        //
        const prog = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(prog);
      },
      (error) => console.log(error),
      () => {
        storage
          .ref("files")
          .child(file.name)
          .getDownloadURL()
          .then((url) => {
            console.log(url)          
                   
          })          
            
          });
      }
    
      const handleFileChosen = (file) => {
        fileReader = new FileReader();
        fileReader.onloadend = handleFileRead;
        fileReader.readAsText(file);
      };
  
