 // Your web app's Firebase configuration
 var firebaseConfig = {
    apiKey: "AIzaSyBhYll44LWToS6AXg6talQ9gwDLe33o9T8",
    authDomain: "login-finance-peer.firebaseapp.com",
    projectId: "login-finance-peer",
    storageBucket: "login-finance-peer.appspot.com",
    messagingSenderId: "555939069142",
    appId: "1:555939069142:web:c33edbebade84614d99d77"
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);

// node refrence
var userID ="userID"
var firebaseRef = firebase.database()

// data to store
var data = {
    username:"wadiemendja",
    password:"UserPassword"
}

// store data in a node with a random unique key
firebaseRef.ref('users/')
firebaseRef.push(data)
/*
root
  |____ users
          |____-MCadGHUbDSEjwx_Ma
                       |_________ username:"wadiemendja"
                       |_________ password:"UserPassword"
*/ 

// store data in a node with a given key
firebaseRef.ref('users/'+userID)
firebaseRef.set(data)
/*
root
  |____ users
          |____userID
                  |___ username:"wadiemendja"
                  |___ password:"UserPassword"
                       
*/ 

// update data
var newData = {
    username:"anotherUsername",
    password:"anotherPassword"
}
var firebaseRef = firebase.database().ref('users/'+userID)
firebaseRef.update(newData)

// remove data
var firebaseRef = firebase.database().ref('users/'+userID)
firebaseRef.remove()

